        点击获取 openid






        上传图片









        前端操作数据库






const app = getApp()

export default {
  name: 'App',
  data() {
    return {
      avatarUrl: require('../images/user-unlogin.png'),
      userInfo: {},
      logged: false,
      takeSession: false,
      requestResult: '',
    }
  },
  mounted() {
    if (!wx.cloud) {
      location.href = '/chooselib'
      return
    }

    // 获取用户信息
    wx.getSetting({
      success: res => {
        if (res.authSetting['scope.userInfo']) {
          // 已经授权，可以直接调用 getUserInfo 获取头像昵称，不会弹框
          wx.getUserInfo({
            success: res => {
              this.avatarUrl = res.userInfo.avatarUrl
              this.userInfo = res.userInfo
            }
          })
        }
      }
    })
  },
  methods: {
    onGetUserInfo(evt) {
      if (!this.logged && evt.detail.userInfo) {
        this.logged = true
        this.avatarUrl = evt.detail.userInfo.avatarUrl
        this.userInfo = evt.detail.userInfo
      }
    },

    onGetOpenid() {
      // 调用云函数
      wx.cloud.callFunction({
        name: 'login',
        data: {},
        success: res => {
          console.log('[云函数] [login] user openid: ', res.result.openid)
          app.globalData.openid = res.result.openid
          window.open('/userconsole')
        },
        fail: err => {
          console.error('[云函数] [login] 调用失败', err)
          window.open('/deployfunctions')
        }
      })
    },

    /**
     * 上传图片
     */
    doUpload() {
      // 选择图片
      wx.chooseImage({
        count: 1,
        sizeType: ['compressed'],
        sourceType: ['album', 'camera'],
        success: res => {
          wx.showLoading({
            title: '上传中',
          })

          const filePath = res.tempFilePaths[0]

          // 上传图片
          const cloudPath = 'my-image' + filePath.match(/\.[^.]+?$/)[0]
          wx.cloud.uploadFile({
            cloudPath,
            filePath,
            success: res => {
              console.log('[上传文件] 成功：', res)

              app.globalData.fileID = res.fileID
              app.globalData.cloudPath = cloudPath
              app.globalData.imagePath = filePath

              window.open('/storageconsole')
            },
            fail: err => {
              console.error('[上传文件] 失败：', err)
              wx.showToast({
                icon: 'none',
                title: '上传失败',
              })
            },
            complete: () => {
              wx.hideLoading()
            }
          })
        },
        fail: e => {
          console.error(e)
        }
      })
    },
  },
}



